mapper.select:
select
from forum_article as a inner join forum_article_type inner join forum_type as t inner join forum_article_user as au inner join forum_user as u where a.status=true and u.uid=au.uid and ( 1=0
or a.title like #{dto.query}
or t.name like #{dto.type}
or u.nickname like #{dto.query}
) group by a.aid
order by page_view desc
order by create_time desc

select
from forum_article as a inner join forum_article_type inner join forum_type as t inner join forum_article_user as au inner join forum_user as u where u.uid=au.uid and a.aid=#{aid}
and a.published=#{published}
and a.status=#{status}
group by a.aid
